<template>
  <main>
    <header class="bg-blue-500 p-4">
      <select
        class="bg-white text-xs border border-gray-200 rounded p-2 float-right"
      >
        <option selected>DE (EUR)</option>
        <option>PL (PLN)</option>
      </select>
      <img
        class="w-32"
        src="data:image/svg+xml;base64,"
      />
    </header>

    <div class="grid grid-cols-3 gap-4 p-4 mb-4">
      <div
        class="bg-white rounded-xl p-4 shadow-md"
        v-for="(product, index) in products"
        :key="product.id"
      >
        <img class="w-32" :src="product.picture" />
        <h3 class="text-xl mb-2">{{ product.name }}</h3>
        <p class="mb-2">{{ product.price }}</p>
        <button
          class="
            py-2
            px-4
            bg-blue-500
            hover:bg-blue-700
            text-white
            rounded-lg
            shadow-md
            float-right
          "
          @click="addToCart(index)"
          :disabled="product.stock < 0"
        >
          Add to cart
        </button>
      </div>
    </div>
    <div class="p-4 m-4 bg-white shadow-md">
      <h2 class="text-2xl mb-2">Cart</h2>
      <hr />
      <table class="table-fixed">
        <thead>
          <tr>
            <th class="w-1/2 text-left p-2">Product</th>
            <th class="w-1/4 p-2">Quantity</th>
            <th class="w-1/4 text-right p-2">Price</th>
          </tr>
        </thead>
        <tbody>
          <tr v-for="product in cart" :key="product.id">
            <td class="p-2">{{ product.name }}</td>
            <td class="p-2">{{ product.quantity }}</td>
            <td class="p-2">{{ product.price }}</td>
          </tr>
        </tbody>
      </table>
      <hr />
      <p class="m-2">Net: 0</p>
      <p class="m-2">VAT: 0</p>
      <p class="m-2 font-bold">Total: {{ total(cart) }}</p>
    </div>
  </main>
</template>

<script>
export default {
  name: "App",
  data: () => ({
    cart: [],
    products: [
      {
        id: 1,
        name: "iPhone 11",
        picture: "https://files.refurbed.com/ii/iphone-11-pro-1619179577.jpg",
        price: 399,
        stock: 3,
      },
      {
        id: 2,
        name: "Samsung Galaxy S8",
        picture:
          "https://files.refurbed.com/ii/64-gb-schwarz-single-sim-1562659918.jpg",
        price: 275,
        stock: 5,
      },
    ],
  }),
  methods: {
    addToCart(id) {
      let product = this.products[id];
      product.quantity = 1;
      product.stock = product.stock - 1;
      console.log(product);
      this.cart.push(product);
    },
    total(products) {
      var total = 0;
      products.forEach((product) => {
        total += product.price;
      });
      return total;
    },
    vat_rates() {
      var requestURL = "https://api.exchangerate.host/vat_rates";
      var request = new XMLHttpRequest();
      request.open("GET", requestURL);
      request.responseType = "json";
      request.send();

      request.onload = function () {
        var response = request.response;
        console.log(response);
      };
    },
    exchangeRates() {
      var requestURL = "https://api.exchangerate.host/latest";
      var request = new XMLHttpRequest();
      request.open("GET", requestURL);
      request.responseType = "json";
      request.send();

      request.onload = function () {
        var response = request.response;
        console.log(response);
      };
    },
  },
  mounted() {
    this.vat_rates();
    this.exchangeRates();
  },
};
</script>